mapper.select:
select aai_seq,aai_id,aai_role from admin_account_info where aai_id like '%${keyword}%'
order by aai_seq desc
limit 10 offset #{offset}

mapper.update:
update reader_info
set rd_status = #{status}
where rd_seq = #{rd_seq}

update reader_info
set rd_pwd = #{pwd}
where rd_seq = #{rd_seq}
